lsr.l #8,d1
move.l CHENA,d2
bset.l d1,d2
move.l d2,CHENA
MDRVIE:
movem.l (sp)+,d1-d7/a0-a6
rte

OPMSET:
move.l d0,-(sp)
move.l #$68,d0
trap #$0f
move.l (sp)+,d0
rts

