char ** saichou(char *);
void main(){
char **s;
char *str="In university I am doing fairly well";
s=saichou(str);
printf("%s:%s\n",*s,*(s+1));
}
char ** saichou(p)
char *p;
{
	int n=0;
	int b1=0,b2=0;
	char *ret[2];
	ret[0]=p;ret[1]=p;
	while(1){
		if(*p!='\0' && *p!=' '){n++;p++;continue;}
			else if(n>b1){b1=n;ret[0]=p-n;}
				else if(n>b2){b2=n;ret[1]=p-n;}
		n=0;
		if (*p=='\0')break;
		p++;
	}
	return(ret);
}

