char *saichou(p)
char *p;
{
    int n=0;
    int b1=0;
    char *ret;
    ret=p;
    while(1){
	if (*p!='\0' && *p!=' '){n++;p++;continue;}
	else if (n>b1){b1=n;for(;n > 0; p--,n--);
			   ret=p;}
	n=0;
	if (*p=='\0')break;
	p++;
    }

    return(ret);
}

