int ransuu(num){
int n;
float n2;
get_time();
n=(c_min+1)*(c_sec+1)*1992%3600;
n2=1.0*n/3600;
return(n2*num);
}
get_time(void){
  struct tm *ltimep;
  int current;
  current = time(&current);
  ltimep = localtime(&current);

  c_year  = ltimep->tm_year + 1900;   
  c_month = ltimep->tm_mon + 1;
  c_day   = ltimep->tm_mday;
  c_hour  = ltimep->tm_hour;
  c_min   = ltimep->tm_min;
  c_sec   = ltimep->tm_sec;
}

