COLLD:
movem.l d1-d7/a0-a6,-(sp)
clr.l d3
move.b d0,d3
lsl.l #3,d3
addi.l #$eb0000,d3
clr.l d4
lsr.l #8,d0
move.b d0,d4
lsl.l #3,d4
addi.l #$eb0000,d4
movea.l d3,a3
movea.l d4,a4
move.w (a3),d3
move.w (a4),d4
sub.w d3,d4
cmp.w d1,d4
bmi COLLDN
swap d1
cmp.w d4,d1
bmi COLLDN
move.w 2(a3),d3
move.w 2(a4),d4
sub.w d3,d4
cmp.w d2,d4
bmi COLLDN
swap d2
cmp.w d4,d2
bmi COLLDN
move.l #1,d0
bra COLLDE
