addi.l #$30,d1
bsr PUTBGC
addi.l #2,d0
swap d1
andi.l #$ffff,d1
divu #10,d2
bne SCOREL
movem.l (sp)+,d0-d7/a0-a6
rts

.data
SPACT:dc.l $000000ff,0,0,0
SCROLA:dc.w 0
.bss

